            get
            {
                var json = "{\"requestCoupon\":{\"type\":\"RCT_SYSTEM\",\"odds\":[2880,2630,2380,3500,3750],\"outcomeIds\":[[2698427285],[2698427367],[2698427400],[2698427444],[2698427524]],\"selection\":[[],[],[],[],[]],\"betsPattern\":\"1111111111111111111111111111111\",\"isUserLoggedIn\":true}}";
                return JToken.Parse(json);
            }
        }

